import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faShoppingBasket,faSearch,faEllipsisH } from "@fortawesome/free-solid-svg-icons";
import {motion} from 'framer-motion'

const Header = ({ }) => {
const[state,setState]=useState()
console.log(state);


  let bgMap = [
    {
     id:1, h:"ORGANIC",heading:'Green Way',desc:'Lorem, ipsum dolor sit amet consectetur adipisicing elit. Deserunt dolore voluptates autem, optio quo tenetur asperiores debitis quibusdam ea. Beatae impedit, ipsa quaerat distinctio eius quis et unde. Blanditiis,',src: bg1,
    },
    { id:2,h:"ORGANIC",heading:'Live Healthy',desc:'Lorem, ipsum dolor sit amet consectetur adipisicing elit. Deserunt dolore voluptates autem, optio quo tenetur asperiores debitis quibusdam ea. Beatae impedit, ipsa quaerat distinctio eius quis et unde. Blanditiis,', h:"kfk",src: bg2 },
    {  id:3,h:"ORGANIC",heading:'Live Organic',desc:'Lorem, ipsum dolor sit amet consectetur adipisicing elit. Deserunt dolore voluptates autem, optio quo tenetur asperiores debitis quibusdam ea. Beatae impedit, ipsa quaerat distinctio eius quis et unde. Blanditiis,',h:"efkf",src: bg3 },

  ];
