import React, { useEffect, useState } from 'react';
import bg1 from '../images/bg-1.jpg'
import bg2 from '../images/bg-2.jpg'
import bg3 from '../images/bg-3.jpg'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faShoppingBasket,faSearch,faEllipsisH } from "@fortawesome/free-solid-svg-icons";
import {motion} from 'framer-motion'

const Header = ({ }) => {
const[state,setState]=useState()
console.log(state);


  let bgMap = [
    {
     id:1, h:"ORGANIC",heading:'Green Way',desc:'Lorem, ipsum dolor sit amet consectetur adipisicing elit. Deserunt dolore voluptates autem, optio quo tenetur asperiores debitis quibusdam ea. Beatae impedit, ipsa quaerat distinctio eius quis et unde. Blanditiis,',src: bg1,
    },
    { id:2,h:"ORGANIC",heading:'Live Healthy',desc:'Lorem, ipsum dolor sit amet consectetur adipisicing elit. Deserunt dolore voluptates autem, optio quo tenetur asperiores debitis quibusdam ea. Beatae impedit, ipsa quaerat distinctio eius quis et unde. Blanditiis,', h:"kfk",src: bg2 },
    {  id:3,h:"ORGANIC",heading:'Live Organic',desc:'Lorem, ipsum dolor sit amet consectetur adipisicing elit. Deserunt dolore voluptates autem, optio quo tenetur asperiores debitis quibusdam ea. Beatae impedit, ipsa quaerat distinctio eius quis et unde. Blanditiis,',h:"efkf",src: bg3 },

  ];
// const janu=()=>{

//   const bg=document.querySelector('.bg')


//   let movie = bgMap[Math.floor(Math.random() * bgMap.length)];

//   setState(movie);

// }

  return (
    <div>
      {bgMap.map((bg)=>{
        return(
          <div   className=' overflow-hidden py-[3%] px-[4%] bg-cover bg ' style={{
            background: `linear-gradient(rgba(0, 0, 0, 0.2), rgba(0, 0, 0, 0.2)), url(${bd.src}) center/cover `,
          }} >
           <div className='flex justify-between text-white items-center' >  
            <div>
              <h2 className='font-AbrilRegular text-[40px]' >MIND HILL</h2>
            </div>
            <div className='flex justify-between w-[50%] items-center font-gorditaMedium ' >

                <h4>HOME</h4>
                <h4>PAGES</h4>
                <h4>SHOP</h4>
                <h4>PORTFOLIO</h4>
                <h4>BLOG</h4>
                <div className='bg-[#FFA27E]  rounded-[50%] w-[50px] h-[50px] flex justify-center items-center' >

                <div  >
                <FontAwesomeIcon icon={faShoppingBasket} className='h-[23px]'  />
               </div>
                </div>
               <div>
               <FontAwesomeIcon icon={faSearch}  />
               </div>
               <div>
               <FontAwesomeIcon icon={faEllipsisH}  />
               </div>

            </div>
           </div>
           <div className='text-white h-[513px] flex items-center' >
            <div>

            <h4 className='text-[25px] tracking-[25px] font-gorditaRegular' >{state?state.h:"ORGANIC"}</h4>
            <h1 className='text-[100px] font-AbrilRegular' >Green Way</h1>
            <p className='font-gorditaRegular my-[4%]' >Lorem, ipsum dolor sit amet consectetur adipisicing elit. Deserunt dolore voluptates autem, optio quo tenetur asperiores debitis quibusdam ea. Beatae impedit, ipsa quaerat distinctio eius quis et unde. Blanditiis,</p>
            <button className='bg-[#94C4F7] py-[1%] px-[3%] font-gorditaMedium more '  >VIEW MORE</button>
            </div>
           </div>

          </div>
        )
      })}

    </div>

  );
};

export default Header;
